mapper.update:
update gc_db_config_info
db_password=#{dbPassword}, db_url=#{dbUrl}, dbDriver=#{dbDriver}, dbUsername=#{dbUsername}, schema=#{schema}, db_app_name=#{dbAppName}, db_type=#{dbType}, enable_status=#{enableStatus}, is_deleted=#{isDeleted}, update_time=#{updateTime}, updator=#{updator},
where id=#{id}

mapper.select:

select id, db_url, db_password, db_driver, db_username, schema, db_type, db_app_name, enable_status, is_deleted,
       create_time, creator, update_time, updator
  from gc_db_config_info
 where 1=1

 and db_type=#{dbType} 
 and enable_status=#{enableStatus} 
 and is_deleted=#{isDeleted} 
order by update_time desc
 limit #{start},#{offset}

select count(1) from gc_db_config_info where 1=1
 and db_type=#{dbType} 
 and enable_status=#{enableStatus} 
 and is_deleted=#{isDeleted}


select id, db_url, db_password, db_driver, db_username, schema, db_type, db_app_name, enable_status, is_deleted,
       create_time, creator, update_time, updator
  from gc_db_config_info
 where 1=1

 and id<>#{id}
 and db_app_name=#{dbAppName}
