mapper.update:
update gc_field_info
field_class_name=#{fieldClassName}, field_entity_name=#{fieldEntityName}, is_primary_field=#{isPrimaryField}, class_name=#{className}, entity_name=#{entityName}, primary_field_name=#{primaryFieldName}, primary_type=#{primaryType}, is_add_field=#{isAddField}, is_edit_field=#{isEditField}, is_query_field=#{isQueryField}, is_list_field=#{isListField}, is_delete_field=#{isDeleteField}, delete_value=#{deleteValue}, not_delete_value=#{notDeleteValue}, update_time=#{updateTime}, updator=#{updator},
where id=#{id}

mapper.select:

select id, job_id, table_id, field_name, field_comment, field_desc, field_class_name, field_entity_name, is_primary_field, primary_type, is_add_field,
       is_edit_field, is_query_field, is_list_field, is_delete_field, delete_value, not_delete_value, create_time, creator, update_time, updator
  from gc_field_info
 where 1=1

 and job_id=#{jobId} 
 and table_id=#{tableId} 
order by update_time desc
 limit #{start},#{offset}

select count(1) from gc_field_info where 1=1
 and job_id=#{jobId} 
 and table_id=#{tableId}
