mapper.update:
update gc_job_info
name=#{name}, comment=#{comment}, alias_name=#{aliasName}, class_name=#{className}, entity_name=#{entityName}, primary_field_name=#{primaryFieldName}, is_nav=#{isNav}, module_id=#{moduleId}, nav_name=#{navName}, add_url=#{addUrl}, edit_url=#{editUrl}, delete_url=#{deleteUrl}, list_url=#{listUrl}, get_url=#{getUrl}, disable_url=#{disableUrl}, enable_url=#{enableUrl}, delete_type=#{deleteType}, enable_status=#{enableStatus}, is_deleted=#{isDeleted}, update_time=#{updateTime}, updator=#{updator},
where id=#{id}

mapper.select:

select id, job_id, name, comment, alias_name, class_name, entity_name, primary_field_name, is_nav, module_id, nav_name,
       add_url, edit_url, delete_url, list_url, get_url, disable_url, enable_url, delete_type,
       enable_status, is_deleted,create_time, creator, update_time, updator
  from gc_table_info
 where 1=1

 and job_id=#{jobId} 
 and module_id=#{moduleId} 
 and enable_status=#{enableStatus} 
 and is_deleted=#{isDeleted} 
order by update_time desc
 limit #{start},#{offset}

select count(1) from gc_table_info where 1=1
 and job_id=#{jobId} 
 and module_id=#{moduleId} 
 and enable_status=#{enableStatus} 
 and is_deleted=#{isDeleted}
