mapper.update:
update sys_role_info
role_name=#{roleName}, role_type=#{roleType}, enable_status=#{enableStatus}, is_deleted=#{isDeleted}, update_time=#{updateTime}, updator=#{updator},
where id=#{id}

mapper.select:
select id, role_name, role_type, enable_status, is_deleted, create_time, creator, update_time, updator
from sys_role_info
where id=#{id}


 select id, role_name, role_type, enable_status, is_deleted, create_time, creator, update_time, updator
 from sys_role_info
 where 1=1

 and role_name=#{roleName} 
 and role_type=#{roleType} 
 and enable_status=#{enableStatus} 
 and is_deleted=#{isDeleted} 
order by update_time desc
 limit #{start},#{offset}

select count(1) from sys_resource_info where 1=1
 and role_name=#{roleName} 
 and role_type=#{roleType} 
 and enable_status=#{enableStatus} 
 and is_deleted=#{isDeleted}


select id, role_name, role_type, enable_status, is_deleted, create_time, creator, update_time, updator
from sys_role_info
where 1=1

and id<>#{id}
and role_name=#{roleName}
